    return {
      firstName: '',
      lastName: '',
      email: '',
      phone: '',
      departmentId: '',
      position: '',
      salary: 0,
      hireDate: new Date(),
      status: 'Active',
      address: {
        street: '',
        city: '',
        state: '',
        zipCode: ''
      }
    };
  }
  loadEmployees() {
    this.employeeResult = this.dataService.searchEmployees(this.searchCriteria, this.employeeResult.currentPage, this.employeeResult.pageSize);
  }
  performSearch() {
    this.employeeResult.currentPage = 1;
    this.loadEmployees();
  }
  clearSearch() {
    this.searchCriteria = {
      searchTerm: '',
      departmentId: '',
      status: ''
    };
    this.performSearch();
  }
  goToPage(page) {
    if (page >= 1 && page <= this.employeeResult.totalPages) {
      this.employeeResult.currentPage = page;
      this.loadEmployees();
    }
  }
  getPageNumbers() {
    const totalPages = this.employeeResult.totalPages;
    const current = this.employeeResult.currentPage;
    const pages = [];
    const start = Math.max(1, current - 2);
    const end = Math.min(totalPages, current + 2);
    for (let i = start; i <= end; i++) {
      pages.push(i);
    }
    return pages;
  }
  editEmployee(employee) {
    this.editingEmployee = employee;
    this.currentEmployee = {
      ...employee,
      address: {
        ...employee.address
      }
    };
    this.showEditForm = true;
  }
  deleteEmployee(employee) {
    if (confirm(`Are you sure you want to delete ${employee.firstName} ${employee.lastName}?`)) {
      this.dataService.deleteEmployee(employee.id);
      this.loadEmployees();
    }
  }
  saveEmployee() {
    // Ensure address is properly initialized
    if (!this.currentEmployee.address) {
      this.currentEmployee.address = {
        street: '',
        city: '',
        state: '',
        zipCode: ''
      };
    }
    if (this.editingEmployee) {
      this.dataService.updateEmployee(this.editingEmployee.id, this.currentEmployee);
    } else {
      this.dataService.createEmployee(this.currentEmployee);
    }
    this.closeForm();
    this.loadEmployees();
  }
  closeForm() {
    this.showAddForm = false;
    this.showEditForm = false;
    this.editingEmployee = null;
    this.currentEmployee = this.getEmptyEmployee();
  }
